targets.rawSql:
SELECT year, COUNT(id) as cars FROM autoria 
WHERE year BETWEEN ${year_min:raw} AND ${year_max:raw} 
AND price BETWEEN ${price_min:raw} AND ${price_max:raw}
AND brand LIKE any(array[${brand:sqlstring}])
AND model LIKE any(array[${model:sqlstring}])
GROUP BY year ORDER BY year asc;

SELECT format('%s %s (%s)', brand, model, year) as name, count(id) as cars, AVG(price) as "Avg. Price" FROM autoria 
  WHERE brand LIKE any(array[${brand:sqlstring}])
        AND model LIKE any(array[${model:sqlstring}])
        AND year BETWEEN ${year_min:raw} AND ${year_max:raw}
        AND mileage BETWEEN ${min_mileage:raw} AND ${max_mileage:raw}
  GROUP BY model, brand, year 
  HAVING AVG(price) BETWEEN ${price_min:raw} AND ${price_max:raw}
  ORDER BY cars DESC;

SELECT city, count(id) as cars, AVG(price) as "Avg. Price" FROM autoria 
  WHERE brand LIKE any(array[${brand:sqlstring}])
        AND model LIKE any(array[${model:sqlstring}])
        AND year BETWEEN ${year_min:raw} AND ${year_max:raw}
  GROUP BY city
  HAVING AVG(price) BETWEEN ${price_min:raw} AND ${price_max:raw}
  ORDER BY cars DESC;

SELECT CAST(CAST(price AS FLOAT)/1000 AS INT) * 1000 as price, COUNT(id) as cars 
      FROM autoria 
      WHERE year BETWEEN ${year_min:raw} AND ${year_max:raw} 
      AND price BETWEEN ${price_min:raw} AND ${price_max:raw}
      AND brand LIKE any(array[${brand:sqlstring}])
      AND model LIKE any(array[${model:sqlstring}])
      GROUP BY CAST(CAST(price AS FLOAT)/1000 AS INT) * 1000  
      ORDER BY CAST(CAST(price AS FLOAT)/1000 AS INT) * 1000 asc;
